    def openMainWindow(self, MainWindow, user_info):
        user_device_info = adbm.userDeviceInfo(user_info)
        self.window_aktarsis_main = QtWidgets.QMainWindow()
        self.ui_aktarsis_main = aktarsis_main.Ui_AktarsisMainWindow()
        self.ui_aktarsis_main.setUserandDeviceInfo(user_info, user_device_info)
        self.ui_aktarsis_main.setToolBarUi(self.window_aktarsis_main)
        self.ui_aktarsis_main.setupUi(self.window_aktarsis_main)
        MainWindow.hide()
        self.window_aktarsis_main.show()

    def openUserAgreement(self):
        self.window_user_agreement = QtWidgets.QMainWindow()
        self.ui_user_agreement = aktarsis_user_agreement.Ui_Aktarsis_User_Agreement()
        self.ui_user_agreement.setupUi(self.window_user_agreement)
        self.window_user_agreement.show()

    def buttonChangePassword(self):
        name = self.lineEdit_3.text()
        surname = self.lineEdit_4.text()
        email = self.lineEdit_5.text()
        password_new = self.lineEdit_6.text()
        password_new_2 = self.lineEdit_7.text()

        result, password_change_info = adbm.userUpdatePassword(name, surname, email, password_new, password_new_2)
        
        if password_change_info:
            self.label_12.setText(result)
            self.label_11.setText("")
        else:
            self.label_12.setText(result)
            self.label_11.setText("Hata:")
    
    def changePasswordTab(self):
        self.tabWidget.setCurrentIndex(1)

    def buttonLogin(self):
        email = self.lineEdit.text()
        password = self.lineEdit_2.text()
        result, login_info, user_info = adbm.userLogin(email, password)
        self.label_4.setText(result)
        
        if login_info:
            self.openMainWindow(MainWindow, user_info)
            self.label_3.setText("")
        else:
            self.label_3.setText("Hata:")
    
    def buttonRegister(self):
        name = self.lineEditReg.text()
        surname = self.lineEdit_reg_2.text()
        email = self.lineEdit_reg_3.text()
        device_id = self.lineEdit_reg_4.text()
        password = self.lineEdit_reg_5.text()
        password_2 = self.lineEdit_reg_6.text()

        if self.checkBox_reg.isChecked():
            label_result, info_result = adbm.userRegisteration(name, surname, email, device_id, password, password_2)
            if info_result:
                self.label_reg_8.setText("")
                self.label_reg_9.setText(label_result)
            else:
                self.label_reg_8.setText("Hata:")
                self.label_reg_9.setText(label_result)
        else:
            self.label_reg_8.setText("Hata:")
            self.label_reg_9.setText("Lütfen Kullanıcı Sözleşmesini Kabul Edin!!!")

    def buttonRegSupport(self):
        self.label_reg_8.setText("")
        self.label_reg_9.setText("Lütfen +905536879398 Numarası ile İrtibata Geçin...")
    
